<script>
export default {
  name: 'ChecksDrawing',
  props: {
    delay: {
      type: Number,
      default: 1
    }
  },
  data() {
    return {
      tl: null
    }
  },
  beforeDestroy() {
    this.$observer.unobserve(this.$el)
  },
  async mounted() {
    this.$observer.observe(this.$el)

    await this.$nextTick()
    this.setAnimation()
  },
  methods: {
    setAnimation() {
      const paths = [
        '#icons-drawing #Path_182',
        '#icons-drawing #Path_183',
        '#icons-drawing #Path_184',
        '#icons-drawing #Path_185',
        '#icons-drawing #Path_186',
        '#icons-drawing #Path_187',
        '#icons-drawing #Path_188',
        '#icons-drawing #Path_189',
        '#icons-drawing #Path_193',
        '#icons-drawing #Path_192'
      ]

      this.tl = new TimelineMax({ paused: true, delay: this.delay })
      this.tl
        .set(paths, { drawSVG: '0%' })
        .set(this.$el, { opacity: 0 })
        .to(this.$el, 0.5, { opacity: 1 })
        .staggerTo(paths, 0.5, { drawSVG: '100%' }, 0.5)
    },
    play() {
      this.tl.play()
    }
  }
}
</script>
